import React, { useState } from 'react';
import QuizData from './QuizData'; // Assuming you have QuizData with quiz questions and answers.

import './styles.css';

function App() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userAnswer, setUserAnswer] = useState(null);
  const [options, setOptions] = useState([]);
  const [quizEnd, setQuizEnd] = useState(false);
  const [score, setScore] = useState(0);
  const [disabled, setDisabled] = useState(true);

  // Component that holds the current quiz
  const loadQuiz = () => {
    const currentQuestion = QuizData[currentIndex];
    setCurrentQuestion(currentQuestion.question);
    setOptions(currentQuestion.options);
    setUserAnswer(null);
  };

  const nextQuestionHandler = () => {
    // Check if correct answer and increment score
    if (userAnswer === QuizData[currentIndex].answer) {
      setScore(score + 1);
    }

    setCurrentIndex(currentIndex + 1);
    setUserAnswer(null);
    setDisabled(true);
  };

  // Check the answer
  const checkAnswer = (answer) => {
    setUserAnswer(answer);
    setDisabled(false);
  };

  const finishHandler = () => {
    if (currentIndex === QuizData.length - 1) {
      setQuizEnd(true);
    }
  };

  if (quizEnd) {
    return (
      <div>
        <h1>Game Over. Final score is {score} points</h1>
        <p>The correct Answers for the quiz are</p>
        <ul>
          {QuizData.map((item, index) => (
            <li className='ui floating message options' key={index}>
              {item.answer}
            </li>
          ))}
        </ul>
      </div>
    );
  }

  return (
    <div>
      <h2>{QuizData[currentIndex].question}</h2>
      <span>{`Question ${currentIndex + 1} of ${QuizData.length}`}</span>
      {options.map((option) => (
        <p
          key={option.id}
          className={`ui floating message options ${
            userAnswer === option ? 'selected' : null
          }`}
          onClick={() => checkAnswer(option)}
        >
          {option}
        </p>
      ))}
      {currentIndex < QuizData.length - 1 && (
        <button
          className='ui inverted button'
          disabled={disabled}
          onClick={nextQuestionHandler}
        >
          Next Question
        </button>
      )}
      {currentIndex === QuizData.length - 1 && (
        <button
          className='ui inverted button'
          disabled={disabled}
          onClick={finishHandler}
        >
          Finish
        </button>
      )}
    </div>
  );
}


export default App;
