import React, { Component } from 'react'
import {QuizData} from './QuizData';

function Quiz(props) {
  const { question, options, currentIndex, userAnswer, quizEnd, score, disabled, checkAnswer, nextQuestionHandler, finishHandler } = props;

  if (quizEnd) {
    return (
      <div>
        <h1>Game Over. Final score is {score} points</h1>
        <p>The correct Answers for the quiz are</p>
        <ul>
          {options.map((item, index) => (
            <li className='ui floating message options' key={index}>
              {item.answer}
            </li>
          ))}
        </ul>
      </div>
    );
  }

  return (
    <div>
      <h2>{question}</h2>
      <span>{`Question ${currentIndex + 1} of ${options.length}`}</span>
      {options.map((option) => (
        <p
          key={option.id}
          className={`ui floating message options ${userAnswer === option ? 'selected' : null}`}
          onClick={() => checkAnswer(option)}
        >
          {option}
        </p>
      ))}
      {currentIndex < options.length - 1 && (
        <button className='ui inverted button' disabled={disabled} onClick={nextQuestionHandler}>
          Next Question
        </button>
      )}
      {currentIndex === options.length - 1 && (
        <button className='ui inverted button' disabled={disabled} onClick={finishHandler}>
          Finish
        </button>
      )}
    </div>
  );
}

export default Quiz;
