import React, { Component } from 'react';
import QuizData from './QuizData';

export class Quiz extends Component {
  state = {
    userAnswer: null,
    currentIndex: 0,
    options: [],
    quizEnd: false,
    score: 0,
    disabled: true,
  };

  // Function to handle user's answer selection
  handleOptionClick = (answer) => {
    // Add logic to handle user's answer selection and update state accordingly
  };

  render() {
    const { currentIndex, quizEnd } = this.state;

    if (quizEnd) {
      // Render quiz results or a "Quiz completed" message
      return <div>Quiz completed!</div>;
    }

    return (
      <div>
        <h2>{QuizData[currentIndex].question}</h2>
        <ul>
          {QuizData[currentIndex].options.map((option, index) => (
            <li key={index} onClick={() => this.handleOptionClick(option)}>
              {option}
            </li>
          ))}
        </ul>
      </div>
    );
  }
}

export default Quiz;
