import React from 'react'
import ReactDOM from 'react-dom'
import Quiz from './components/Quiz'

import './styles.css';

function App() {
    return (
        <div className="App">
            <Quiz />
        </div>
    )
}

const rootElement = document.getElementById('root');
ReactDOM.render(<App></App>, rootElement) //renders the component(first parameter) as a child of the element(second parameter)
state = {
    userAnswer:null, //current users answer
    currentIndex:0,  //current questions index
    options: [],     //the four options
    quizEnd: false,  //determines if it's the last question
    score: 0,        //holds the score
    disabled: true   // determines the status of the buttons
}
